一個 VHDL 程式碼如下,請寫出這程式所描述元件的功能表(或稱真值表)。
library ieee; use ieee.std_logic_1164.all;
entity aaa is
port (clk_n, CE,
pre_n, clr_n, D: in std_logic;
Q, Q_n : out std_logic);
end entity aaa;
architecture behavior of aaa is
signal Q_tmp: std_logic;
begin
bb: process (clk_n, pre_n, clr_n) is
begin
assert not (pre_n = '0' and clr_n = '0')
report "Illegal inputs: pre_n and clr_n both '0'";
if pre_1 = '0' then
Q_tmp <= '1';
elsif clr_n = '0' then
Q_tmp <= '0';
elsif falling_edge(clk_n) then
if CE = '1' then
Q_tmp <= D;
end if;
end if;
end process bb;
Q <= Q_tmp; Q_n <= not Q_tmp;
end architecture behavior;

